    @Test
    public void g() {
        int n = 4000;
        int maxSlaves = 999;
        int headID = (int)(Math.random()*n);
        Set<Integer> set = new HashSet<>();
        List<Integer> employees = new ArrayList<>();
        for (int i = 0; i < n; i++) employees.add(i);
        Collections.shuffle(employees);
        Map<Integer, Integer> map = new HashMap<>();
        map.put(headID, -1);
        set.add(headID);
        while (map.size() < n) {
            Iterator<Integer> it = set.iterator();
            Set<Integer> temp = new HashSet<>();
            while (it.hasNext()) {
                int manager = it.next();
                it.remove();
                employees.remove((Object)manager);
                int rand = (int)(Math.random()*maxSlaves + 1);
                while (employees.size() - rand < 0) {
                    maxSlaves--;
                    rand = (int)(Math.random()*maxSlaves + 1);
                }
                int start = employees.size() - rand;
                for (int j = start; j < employees.size(); j++) {
                    int empl = employees.get(j);
                    map.put(empl, manager);
                    temp.add(empl);
                }
            }
            set.addAll(temp);
        }
        int[] result = new int[n];
        for (int key : map.keySet()) result[key] = map.get(key);
        for (int i : result) System.out.print(i + ",");
        System.out.println();
        int[] times = new int[n];
        for (int i : result) if (i != -1) times[i] = (int)(Math.random()*100);
        for (int i : times) System.out.print(i + ",");
    }
