package dynamicProgramming;

import org.junit.Test;
import static org.junit.Assert.assertEquals;

public class NumberOfGoodWaysToSplitStringTest {
    @Test
    public void getWays() {
        assertEquals(0, new NumberOfGoodWaysToSplitString().getWays("a"));
        assertEquals(1, new NumberOfGoodWaysToSplitString().getWays("ab"));
        assertEquals(0, new NumberOfGoodWaysToSplitString().getWays("acd"));
        assertEquals(1, new NumberOfGoodWaysToSplitString().getWays("acc"));
        assertEquals(3, new NumberOfGoodWaysToSplitString().getWays("aaaa"));
        assertEquals(1, new NumberOfGoodWaysToSplitString().getWays("dzdz"));
        assertEquals(1, new NumberOfGoodWaysToSplitString().getWays("bttf"));
        assertEquals(0, new NumberOfGoodWaysToSplitString().getWays("rtyui"));
        assertEquals(1, new NumberOfGoodWaysToSplitString().getWays("assfb"));
        assertEquals(1, new NumberOfGoodWaysToSplitString().getWays("ssrrkk"));
        assertEquals(3, new NumberOfGoodWaysToSplitString().getWays("qwwqqw"));
        assertEquals(1, new NumberOfGoodWaysToSplitString().getWays("xwfmkpqkahnsuin"));
        assertEquals(0, new NumberOfGoodWaysToSplitString().getWays("htztzyxmrqwzmwhnmgqjvphsl"));
        assertEquals(22, new NumberOfGoodWaysToSplitString().getWays("ywvzzxzwxzwvzwwyxvwyvwvwxzwzxxwxvyw"));
        assertEquals(9976, new NumberOfGoodWaysToSplitString().getWays(""));
    }
    @Test
    public void generate() {
        for (int i = 0; i < 10000; i++) {
            char letter = (char)((int) (Math.random()*7+13) + 'a');
            System.out.print(letter);
        }
    }
}
